import { expect } from 'chai';
import nock from 'nock';

import formData from 'form-data';
import EventClient from '../lib/events';
import MgRequest from '../lib/request';
import { RequestOptions } from '../lib/types/RequestOptions';
import { InputFormData } from '../lib/types/IFormData';
import { EventsList, EventsResponse } from '../lib/types/Events';

describe('EventsClient', function () {
  let client: EventClient;
  let api: nock.Scope;

  beforeEach(function () {
    client = new EventClient(new MgRequest({ url: 'https://api.mailgun.net' } as RequestOptions, formData as InputFormData));
    api = nock('https://api.mailgun.net');
  });

  afterEach(function () {
    api.done();
  });

  describe('GET', function () {
    let response: any;

    beforeEach(function () {
      response = {
        items: [
          {
            event: 'accepted',
            timestamp: 'Wed, 19 Nov 2014 18:32:57 GMT',
            content: { more: 'data' }
          }, {
            event: 'opened',
            timestamp: 'Tue, 18 Nov 2014 12:32:57 GMT',
            content: { more: 'data' }
          }
        ],
        paging: {
          first: 'https://api.mailgun.net/v2/mailgun.',
          last: 'https://api.mailgun.net/v2/mailgun.',
          next: 'https://api.mailgun.net/v2/mailgun.',
          previous: 'https://api.mailgun.net/v2/mailgun.'
        }
      };
    });

    it('fetches all events', function () {
      api.get('/v3/domain.com/events').reply(200, response);

      return client.get('domain.com').then(function (data: EventsList) {
        let e;

        e = data.items[0];
        e.event.should.eql('accepted');
        e.timestamp.should.eql('Wed, 19 Nov 2014 18:32:57 GMT');

        e = data.items[1];
        e.event.should.eql('opened');
        e.timestamp.should.eql('Tue, 18 Nov 2014 12:32:57 GMT');
      });
    });

    it('fetches single page', function () {
      api.get('/v3/domain.com/events/pageId').reply(200, response);

      return client.get('domain.com', { page: 'pageId' }).then(function (data: EventsList) {
        let e;

        e = data.items[0];
        e.event.should.eql('accepted');
        e.timestamp.should.eql('Wed, 19 Nov 2014 18:32:57 GMT');

        e = data.items[1];
        e.event.should.eql('opened');
        e.timestamp.should.eql('Tue, 18 Nov 2014 12:32:57 GMT');
      });
    });

    it('parses page links', async function () {
      api.get('/v3/domain.com/events').reply(200, response);
      const result: EventsList = await client.get('domain.com');

      result.pages.first.url.should.eql('https://api.mailgun.net/v2/mailgun.');
      result.pages.first.page.should.eql('');
      expect(result.pages.first.iteratorPosition).to.be.equal(null);

      result.pages.last.url.should.eql('https://api.mailgun.net/v2/mailgun.');
      result.pages.last.page.should.eql('');
      expect(result.pages.last.iteratorPosition).to.be.equal(null);

      result.pages.next.url.should.eql('https://api.mailgun.net/v2/mailgun.');
      result.pages.next.page.should.eql('');
      expect(result.pages.next.iteratorPosition).to.be.equal(null);

      result.pages.previous.url.should.eql('https://api.mailgun.net/v2/mailgun.');
      result.pages.previous.page.should.eql('');
      expect(result.pages.previous.iteratorPosition).to.be.equal(null);
    });
  });
});
